import React, { Component } from 'react'
import axios from 'axios'

class Home extends Component {
    state = {
        products:{},
    }
    loadData = () => {
        const data = 
            axios.get('http://localhost:4200/product/')
            .then(function (response) {
                // handle success
               // console.log(response.data);
                let products = response.data;
                this.setState({products})
            })
            .catch(function (error) {
                // handle error
                console.log(error);
            })
            .finally(function () {
                // always executed
            })
    }

    render () {

        console.log(this.state.products)

        return (
        <div className="card">
                <div className="card-header">
                    <ul className="nav nav-pills w-100">
                        <li className="nav-pill active">
                            <a className="nav-link">les articles</a>
                        </li>
                        <li className="nav-pill ml-auto">
                            <a className="nav-link active" href="/books/add">Add Book</a>
                        </li>
                    </ul>
                </div>

            <div className="card-body">
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col" width="300px">Image</th>
                            <th scope="col">Name</th>
                            <th width="200px">Action</th>
                        </tr>
                    </thead>
                    <tbody> 

                    
       
                    
                      <tr>
                            <td><img className="fit-picture" width="180px"
                                        src="https://www.afflelou.com/fstrz/r/s/media.afflelou.com/cbz_K2vVsLlLRbm7mApJm8AXuF0=/840x430/afflelou/france/products/00664689793044_face.png?frz-v=825"
                                        alt="lunette"/>
                            </td>
                            <td>lunette</td>
                            <td>
                                <button className="btn btn-success edit" >Edit </button>
                                <button className="btn btn-danger delete"  >Delete</button>
                            </td>
                        </tr>

                        <tr>
                            <td><img className="fit-picture" width="180px"
                                        src="https://www.afflelou.com/fstrz/r/s/media.afflelou.com/cbz_K2vVsLlLRbm7mApJm8AXuF0=/840x430/afflelou/france/products/00664689793044_face.png?frz-v=825"
                                        alt="lunette"/>
                            </td>
                            <td>lunette</td>
                            <td>
                                <button className="btn btn-success edit" >Edit </button>
                                <button className="btn btn-danger delete">Delete</button>
                            </td>
                        </tr>

                    </tbody>
                </table>
            </div>
        </div>
        )
    }
}

export default Home
